import React from "react";
import "./styles.css";
import Button from "./components/Button";
import { useDispatch, useSelector } from "react-redux";
import { RootState } from "./store";
import {
  addDigit,
  chooseOperation,
  clearDigit,
  removeLastDigit,
} from "./store/features/digitSlice";
import ClearButton from "./components/ClearButton";
import RemoveLastDigitButton from "./components/RemoveLastDigitButton";
import OperationButton from "./components/OperationButton";

function App() {
  // const [pressedDigit, setPressedDigit] = useState<string>('')

  const digits = useSelector((state: RootState) => state.digits);

  const dispatch = useDispatch();



  const handleAddDigits = (pressedDigit: string) => {
    dispatch(addDigit(pressedDigit));
    console.log("hello")
    console.log(digits);



  };

  const handleClearDigit = () => {
    dispatch(clearDigit());
  };

  const handleRemoveLastDigit = () => {
    console.log("hello")
    dispatch(removeLastDigit());
  };

 const  handleChooseOperation=(pressedOperation:string)=>{

    dispatch(chooseOperation(pressedOperation));

    console.log("operation clicked", digits.prevOperand)



  }

  return (
    // grid container
    <div className="container">
      {/* output screen of calculator */}
      <div className="container__screen">
        <div className="container__screen--calculation">{digits.prevOperand}</div>

        <div className="container__screen--result">{ digits.currentOperand }</div>
      </div>

      <ClearButton
        digit="AC"
        className=" btn--span-two btn--white"
        handleClearDigit={handleClearDigit}
      />
      <RemoveLastDigitButton
        digit="DEL"
        className="btn--white"
        handleRemoveLastDigit={handleRemoveLastDigit}
      />

      <OperationButton operation="รท" className="btn--cirlcular-border" />

      <Button
        digit="1"
        className="btn--white"
        handleAddDigits={handleAddDigits}
      />
      <Button
        digit="2"
        className="btn--white"
        handleAddDigits={handleAddDigits}
      />
      <Button
        digit="3"
        className="btn--white"
        handleAddDigits={handleAddDigits}
      />

      <OperationButton handleChooseOperation={handleChooseOperation} operation="ร" className="btn--cirlcular-border" />

      <Button
        digit="4"
        className="btn--white"
        handleAddDigits={handleAddDigits}
      />
      <Button
        digit="5"
        className="btn--white"
        handleAddDigits={handleAddDigits}
      />
      <Button
        digit="6"
        className="btn--white"
        handleAddDigits={handleAddDigits}
      />

      <OperationButton operation="+" className="btn--cirlcular-border " handleChooseOperation={handleChooseOperation} />

      <Button
        digit="7"
        className="btn--white"
        handleAddDigits={handleAddDigits}
      />
      <Button
        digit="8"
        className="btn--white"
        handleAddDigits={handleAddDigits}
      />
      <Button
        digit="9"
        className="btn--white"
        handleAddDigits={handleAddDigits}
      />

      <OperationButton operation="-" className="btn--cirlcular-border" />

      <Button
        digit="."
        className="btn--white"
        handleAddDigits={handleAddDigits}
      />

      <Button
        digit="0"
        className="btn--white"
        handleAddDigits={handleAddDigits}
      />

      <Button
        digit="="
        className=" btn--span-threeTofour btn--cirlcular-border"
      />
    </div>
  );
}

export default App;
