import {  useDispatch, useSelector  } from "react-redux";
import { RootState } from "../store";
import { addDigit, chooseOperation, clearDigit, performEvaluation, removeLastDigit } from "../store/features/digitSlice";


export const digits = useSelector((state: RootState) => state.digits);


 const dispatch = useDispatch();

export const handleAddDigits = (pressedDigit: string) => {
    dispatch(addDigit(pressedDigit));
    
    console.log(digits.currentOperand);
  };

 export const handleClearDigit = () => {
    dispatch(clearDigit());
  };

 export const handleRemoveLastDigit = () => {
    
    dispatch(removeLastDigit());
  };

 export const handleChooseOperation = (pressedOperation: string) => {
    dispatch(chooseOperation(pressedOperation));

    console.log("operation clicked", digits.operation);
  };

 export const handleEvaluation = () => {
    dispatch(performEvaluation());
  };
