import { createSlice ,PayloadAction } from "@reduxjs/toolkit";


interface digit{
    currrentOperand:string;
        prevOperand: string;
        operation:string;
    }

interface digitState{
    value: digit
}







const initialState : digitState= {
  
    value:{
        currrentOperand:"",
        prevOperand:"",
        operation:""
    }
    

}





export const digitSlice =  createSlice({

    name :"digits",
    initialState,

    reducers:{

        addDigit:(state , action:PayloadAction<string>)=>{
            if(state.value.currrentOperand === '0'&& action.payload ==='0'){
                return {
                    ...state,
                    currentOperand:""
                };

            }
            if(state.value.currrentOperand.includes('.')  && action.payload ==='.'){
                return state;

            }
        return  {
                currentOperand:`${state.value.currrentOperand || "" }${action.payload}`
            }
        },
        clearDigit:()=>{
             
            return {
                currentOperand:""
            }

        },
        removeLastDigit: (state )=>{

            return {
                currentOperand: `${state.value.currrentOperand.slice(0, -1)}`
            }
    
        }

        

    }
   


}) 


export const {addDigit ,clearDigit ,removeLastDigit}= digitSlice.actions


export default digitSlice.reducer
