import { createSlice ,PayloadAction } from "@reduxjs/toolkit";



interface CalculatorState{
    currrentOperand:string;
        prevOperand?: string;
        operation?:string;
    }








const initialState : CalculatorState= {
  
    
        currrentOperand:"",
        prevOperand:"",
        operation:""
    
    

}


const Evaluate= (currrentOperand:string , prevOperand:string ,operation:string) => {

    const current = parseFloat(currrentOperand)
    const prev = parseFloat(prevOperand)

    if(isNaN(current) || isNaN(prev)) return ""
 
       let result: string | number=""

    switch (operation){
        case "+":
            result=  prev + current
            break
        case "-":
            result=  prev - current
            break
        case "x":
            result=  prev * current
            break

        case "รท":
            result=  prev / current
            break
}

    return result.toString()

}





export const digitSlice =  createSlice({

    name :"CalculatorState",
    initialState,

    reducers:{

        addDigit:(state , action:PayloadAction<string>)=>{
            if(state.currrentOperand === '0'&& action.payload ==='0'){
                return  state;

            }
            if(state.currrentOperand.includes('.')  && action.payload ==='.'){
                return state;

            }
        return  {
        
                
                    ...state,
                    currrentOperand:`${state.currrentOperand || "" }${action.payload}` 
                    
                                     
                
                    
                
            }
        },

        clearDigit:(state)=>{
             
            return {
                ...state,
                currrentOperand:"",
                prevOperand:"",
                operation:""

                
            }

        },
        removeLastDigit: (state )=>{
            const remainingDigit:string=`${state.currrentOperand.slice(0, -1)}`

            return   { ...state, currrentOperand:remainingDigit }
                
               
                
            
    
        },
        chooseOperation:(state , action:PayloadAction<string> ) => {

            if(state.prevOperand === ""){

                return{ ...state , operation:action.payload ,prevOperand:state.currrentOperand ,currrentOperand:""}

            }

            return
        }
    
        

    }
   


}) 


export const {addDigit ,clearDigit ,removeLastDigit ,chooseOperation}= digitSlice.actions


export default digitSlice.reducer
