import React from "react";

interface ButtonProps {
  digit: string;
  handleAddDigits?: (pressedDigit: string) => void;
  handleClearDigit?: () => void;
  className?: string;
  setPressedDigit?: React.Dispatch<React.SetStateAction<string>>;
}

const Button = ({
  digit,
  className,
  handleAddDigits,
  setPressedDigit,
  handleClearDigit
}: ButtonProps) => {
  return (
    <button className={className} onClick={() => { handleAddDigits?.(digit), handleClearDigit()} }>
      {digit}
    </button>
  );
};

export default Button;
