import React from "react";
import "./styles.css";
import Button from "./components/Button";
import { useDispatch, useSelector } from "react-redux";
import { RootState } from "./store";
import {

  digits,
  dispatch,
  handleAddDigits,
  handleClearDigit

} from "./utils/handleDigitsOperation";
import ClearButton from "./components/ClearButton";
import RemoveLastDigitButton from "./components/RemoveLastDigitButton";
import OperationButton from "./components/OperationButton";
import EvaluateButton from "./components/EvaluateButton";
import {formatStringtoNumberWithComma} from "./utils/digitFormater"

function App() {








  return (
    // grid container
    <div className="container">
      {/* output screen of calculator */}
      <div className="container__screen">
        <div className="container__screen--calculation">
          {formatStringtoNumberWithComma(digits.prevOperand)}
          {digits.operation}
        </div>

        <div className="container__screen--result">
          {formatStringtoNumberWithComma(digits.currentOperand)}
        </div>
      </div>

      <ClearButton
        digit="AC"
        className=" btn--span-two btn--white"
        handleClearDigit={handleClearDigit}
      />
      <RemoveLastDigitButton
        digit="DEL"
        className="btn--white"
        handleRemoveLastDigit={handleRemoveLastDigit}
      />

      <OperationButton
        operation="รท"
        className="btn--cirlcular-border"
        handleChooseOperation={handleChooseOperation}
      />

      <Button
        digit="1"
        className="btn--white"
        handleAddDigits={handleAddDigits}
      />
      <Button
        digit="2"
        className="btn--white"
        handleAddDigits={handleAddDigits}
      />
      <Button
        digit="3"
        className="btn--white"
        handleAddDigits={handleAddDigits}
      />

      <OperationButton
        handleChooseOperation={handleChooseOperation}
        operation="ร"
        className="btn--cirlcular-border"
      />

      <Button
        digit="4"
        className="btn--white"
        handleAddDigits={handleAddDigits}
      />
      <Button
        digit="5"
        className="btn--white"
        handleAddDigits={handleAddDigits}
      />
      <Button
        digit="6"
        className="btn--white"
        handleAddDigits={handleAddDigits}
      />

      <OperationButton
        operation="+"
        className="btn--cirlcular-border "
        handleChooseOperation={handleChooseOperation}
      />

      <Button
        digit="7"
        className="btn--white"
        handleAddDigits={handleAddDigits}
      />
      <Button
        digit="8"
        className="btn--white"
        handleAddDigits={handleAddDigits}
      />
      <Button
        digit="9"
        className="btn--white"
        handleAddDigits={handleAddDigits}
      />

      <OperationButton
        operation="-"
        className="btn--cirlcular-border"
        handleChooseOperation={handleChooseOperation}
      />

      <Button
        digit="."
        className="btn--white"
        handleAddDigits={handleAddDigits}
      />

      <Button
        digit="0"
        className="btn--white"
        handleAddDigits={handleAddDigits}
      />

      <EvaluateButton
        equalDigit="="
        handleEvaluation={handleEvaluation}
        className=" btn--span-threeTofour btn--cirlcular-border"
      />
    </div>
  );
}

export default App;
